		group "external/wxWidgets"
			for index, proj in pairs(wxWidgetsProjects) do
				project(proj.name)
					location("build/".._ACTION)
					kind "StaticLib"
					language "C++"									
					
					-- local useful variables.
					local PREPROCESSOR = { "_LIB", "_CRT_SECURE_NO_DEPRECATE=1", "_CRT_NON_CONFORMING_SWPRINTFS=1", "_SCL_SECURE_NO_WARNINGS=1", proj.preprocessor }
					local commonFlags = { }
					local unicodePostfix = ""
					
					-- precompiled header
					if proj.nopch ~= true then
						pchheader("include/wx/wxprec.h")
						pchheadername("wx/wxprec.h")
						pchsource("src/common/dummy.cpp")
					end
					
					-- set specific platform configuration.				
					if os.is("windows") == true then
						table.insert(PREPROCESSOR, "_WXMSW_")				
						table.insert(PREPROCESSOR, "WIN32")
					elseif os.is("linux") == true then
						table.insert(PREPROCESSOR, "_WXX11_")
					end
					
					if proj.unicode then
						table.insert(PREPROCESSOR, "_UNICODE")
						table.insert(commonFlags, "unicode")
						unicodePostfix = "u"
					end
					
					-- include and library directories
					local includes = {}
					local libraries = {}
					local libraries_dir = {}
					--[[for i, dep in pairs(proj.dependencies) do
						if dependencies[""..dep] ~= nil then
							table.insert(libraries, dependencies[""..dep].lib)
							table.insert(includes, dependencies[""..dep].include)
						
							if dependencies[""..dep].libdir then
								table.insert(libraries_dir, dependencies[""..dep].libdir)
							end
						end
					end]]
					includedirs { "include/", "setup/", includes, proj.includes }			
					
					
					------------------------------------------------------------
					-------------------------------------------------  ADD FILES
					-- create virtual folder.
					--vpaths { proj.vpaths[1] }
						
					local projectFiles = {}
					for index, vpath in pairs(proj.vpaths) do
						for i, file in pairs(vpath) do
							table.insert(projectFiles, file)
						end
					end
					files {
						projectFiles
					}		

					filesnopch {
						proj.filesnopch
					}
					------------------------------------------------------------
					
					
					------------------------------------------------------------
					------------------------------------------ CUSTOM BUILD RULE
					configuration "include/wx/msw/genrcdefs.h"
						buildrule {
							description = "Creating setup/wx/msw/rcdefs.h",
							commands = {
								'cl /EP /nologo "%(FullPath)" > "../../setup/wx/msw/rcdefs.h"'							
							},
							outputs = {
								"../../setup/wx/msw/rcdefs.h;%(Outputs)"
							},
						}
					------------------------------------------------------------
					
					
					------------------------------------------------------------
					--------------------------------------------  CONFIGURATIONS
					configuration "Debug"
						defines { DEBUG_DEF, PREPROCESSOR }
						flags { "Symbols", commonFlags }
						objdir("obj/debug/"..proj.name)
						implibdir "../../lib/debug/"
						targetdir "../../lib/debug/"
						targetname("wx"..proj.name..unicodePostfix)
						libdirs { "../../lib/debug/", libraries_dir }
						links { libraries }
						
					configuration "Profile"
						defines { "NDEBUG", PREPROCESSOR }
						flags { "Symbols", commonFlags }
						objdir("obj/profile/"..proj.name)
						implibdir "../../lib/profile/"
						targetdir "../../lib/profile/"
						targetname("wx"..proj.name..unicodePostfix)
						libdirs { "../../lib/profile/", libraries_dir }
						links { libraries }
						
					configuration "Release"
						defines { "NDEBUG", PREPROCESSOR }
						flags { "Optimize", "FatalWarnings", commonFlags }
						objdir("obj/release/"..proj.name)
						implibdir "../../lib/release/"
						targetdir "../../lib/release/"
						targetname("wx"..proj.name..unicodePostfix)
						libdirs { "../../lib/release/", libraries_dir }
						links { libraries }
